pragma solidity ^0.8.9;

library LibTokenConst {
	//Pausable
	bytes32 public constant FULL = 0x00;

	//Roles
	bytes32 public constant TOKEN_ADMIN_ROLE = keccak256("TOKEN_ADMIN_ROLE");
